package invalid.showme.tests;

import junit.framework.Assert;

import java.io.ByteArrayInputStream;
import java.io.UnsupportedEncodingException;

import invalid.showme.util.IOUtil;

public class IOUtilTest extends TestCaseWithUtils
{
    public void testReadFully() throws UnsupportedEncodingException {
        String input1 = "123456 7890";
        ByteArrayInputStream bs1 = new ByteArrayInputStream(input1.getBytes("UTF-8"));
        String output1 = new String(IOUtil.readFully(bs1), "UTF-8");
        Assert.assertEquals(input1, output1);

        String input2 = "";
        ByteArrayInputStream bs2 = new ByteArrayInputStream(input2.getBytes("UTF-8"));
        String output2 = new String(IOUtil.readFully(bs2), "UTF-8");
        Assert.assertEquals(input2, output2);

        String input3 = "";
        ByteArrayInputStream bs3 = new ByteArrayInputStream(input3.getBytes("UTF-8"));
        String output3 = new String(IOUtil.readFully(bs3), "UTF-8");
        Assert.assertEquals(input3, output3);

        String input4 = "";
        ByteArrayInputStream bs4 = new ByteArrayInputStream(input4.getBytes("UTF-8"));
        String output4 = new String(IOUtil.readFully(bs4), "UTF-8");
        Assert.assertEquals(input4, output4);

        byte[] input5 = new byte[] { 0x00, 0x01, 0x03, 0x05, 0x06 };
        ByteArrayInputStream bs5 = new ByteArrayInputStream(input5);
        byte[] output5 = IOUtil.readFully(bs5);
        Assert.assertTrue(CompareArrays(input5, output5));
    }
}
